$(document).ready(function() {
    $('a.callback').prop('disabled', false);

    $('body').on('click', 'a.callback', function(e) {
        e.preventDefault();
        var $this = $(this);

        if($this.prop('disabled'))
            return false;

        var button_title = $this.text();
        $this.prop('disabled', true);
        $this.text('Отправка...');
        $this.addClass('idle');

        $.ajax({
            url: '/callback/',
            type: 'POST',
            data: $this.data(),
            success: function(json) {
                $this.prop('disabled', false);
                $this.text(button_title);
                $this.removeClass('idle');

                if(json.result != 'ok')
                {
                    if (json.message)
                        show_error(json.message);

                    return;
                }

                if (json.action)
                    eval(json.action);

                if (json.message)
                    show_ok(json.title, json.message, json.redirect);
                else if (json.redirect)
                    location.href = json.redirect;
                else if (json.redirect == "")
                    location.reload();
            }
        });

        return false;
    });

    $('form.callback [type=submit]').prop('disabled', false);
    
    $('form.callback').submit(function(e) {
        e.preventDefault();

        var $this = $(this);
        $this.find('.has-error').removeClass('has-error');
        var button_title = $this.find('button[type=submit]').text();

        $this.find('button[type=submit]').prop('disabled', true);
        $this.find('button[type=submit]').text('Отправка...');
        $this.find('button[type=submit]').addClass('idle');
        
        //Яндекс-цель
        var yandexTarget = $this.data('ya-target');
        
        var options = {
            type: 'POST',
            url: '/callback/',
            success:    function(json) {

                $this.find('button[type=submit]').prop('disabled', false);
                $this.find('button[type=submit]').text(button_title);
                $this.find('button[type=submit]').removeClass('idle');

                if(json.result != 'ok')
                {
                    if (json.message)
                        show_error(json.message);

                    if(typeof(json.fields) != 'undefined')
                    {
                        json.fields.every(function(el, i) {
                            $this.find('[name='+el+']').parent().addClass('has-error');
                            return true;
                        });
                    }

                    return;
                }

                if (json.action)
                    eval(json.action);

                if (typeof(yandexCounter) != "undefined" && typeof(yandexTarget) != "undefined")
            	{
            		yandexCounter.reachGoal(yandexTarget, {}, function () {
            	        console.log('В Метрику успешно отправлен запрос по ключу ' + yandexTarget);
            	    }); 
            	}
                
                if (json.message)
                {
                    show_ok(json.title, json.message, json.redirect, json.button);
                    if ($('.modal-popup').length)
                        $('.modal-popup').hide();
                    if ($('.popup-feedback').length)
                        $('.popup-feedback').hide();
                }
                else if (json.redirect)
                    location.href = json.redirect;
                else if (json.redirect == "")
                    location.reload();

                $this[0].reset();
            },

            error: function() {
                $this.find('button[type=submit]').prop('disabled', false);
                $this.find('button[type=submit]').text(button_title);
                $this.find('button[type=submit]').removeClass('idle');
                show_error('Ошибка отправки данных.<br>Повторите отправку чуть позже');
            }
        };

        $(this).ajaxSubmit(options);
    });


    $('.popup-city button').on('click', function(e) {
        e.preventDefault();

        var button = $(this);

        button.prop('disabled', true);
        var options = {
            type: 'POST',
            url: '/callback/',
            data: { handler: button.data('handler'), city_alias: button.data('cityalias') },
            success:  function(json) {
                button.prop('disabled', false);
                location.href = json.redirect;
            },

            error: function() {
                button.prop('disabled', false);
                show_error('Ошибка отправки данных.<br>Повторите отправку чуть позже');
            }
        };

        $(this).ajaxSubmit(options);
    });


    $('.loadmore').on('click', function(e){
        e.preventDefault();
        var $this = $(this);

        window.history.pushState("","", $this.attr('href'));

        var button_title = $this.text();

        $this.prop('disabled', true);
        $this.text('Загрузка...');
        $this.addClass('idle');

        $.ajax({
            type: "POST",
            url: "/callback/",
            dataType: "json",
            data: {
                handler: $this.data('handler'),
                table: $this.data('table'),
                page: $this.data('page'),
                onpage: $this.data('onpage'),
                baseurl: $this.data('url')
            },
            success: function(result){

               $this.prop('disabled', false);
               $this.text(button_title);
               $this.removeClass('idle');


               if (result.html)
                {
                    if ($('.wrapper-' + $this.data('table')).length)
                    {
                        $('.wrapper-' + $this.data('table')).append(result.html);
                    }
                }
                if (result.page)
                {
                    if (result.page == -1)
                        $this.hide();
                    else
                    {
                        $this.data('page', result.page);
                        if (result.url)
                        {
                            $this.attr('href', result.url);
                        }
                    }
                }
            },
            error: function() {
                $this.prop('disabled', false);
                $this.text(button_title);
                $this.removeClass('idle');
            }
        });
    });


    $('.openPopupOrderMaterial').on('click', function(e){
        e.preventDefault();
        var material = parseInt($(this).data('material'));

        $('#orderMaterials #materialID').val(material);
        $('#orderMaterials').show();

    });
    
    
    
    $('.openPopupOrderService').on('click', function(e){
        e.preventDefault();
        var service = parseInt($(this).data('service'));

        $('#orderService #serviceID').val(service);
        $('#orderService').show();

    });

});

function show_error(message) {
    if (!message)
        return;

    $.toast({
        text: message,
        heading: 'Ошибка',
        showHideTransition: 'fade',
        allowToastClose: true,
        hideAfter: 7000,
        stack: 5,
        position: 'bottom-right',

        bgColor: '#E31D24',
        textColor: '#ffffff',
        textAlign: 'left',
        beforeShow: function () {},
        afterShown: function () {},
        beforeHide: function () {},
        afterHidden: function () {}
    });
}

function show_ok(title, message, redirect, button, width) {

    if (typeof (width) != 'undefined')
        $('#modalMessage .modal-message').width(width);

    if (typeof (title) != 'undefined')
        $('#modalMessage .modal-message-title').html(title);

    if (typeof (message) != 'undefined')
        $('#modalMessage .modal-message-text').html(message);

    if (typeof (button) != 'undefined')
        $('#modalMessage .button').text(button);

    if (!redirect || redirect.length == 0)
    {    
    	$('a.modal-close, a.button').attr('href', '');
    	
    }
    else
        $('a.modal-close, a.button').attr('href', redirect);

    $('#modalMessage').show();
}
