var wWindow = 0, hWindow = 0;

function calc_max_height()
{
    if ($('.max-height-row').length && wWindow > 749)
    {
        $('.max-height-row').each(function(){
            var objs = $(this).find('.max-height-item ');
            objs.height(Math.max.apply(null, objs.map(function ()
            {
                return $(this).height();
            }).get()));
        });
    }
}

